import{f as e,c as l,o as n,a1 as r}from"./chunks/framework.BnRw5oCp.js";const x=JSON.parse('{"title":"敬语","description":"","frontmatter":{},"headers":[],"relativePath":"日语/敬语.md","filePath":"日语/敬语.md"}'),d={name:"日语/敬语.md"};function a(c,t,i,s,o,y){return n(),l("div",null,t[0]||(t[0]=[r('<h1 id="敬语" tabindex="-1">敬语 <a class="header-anchor" href="#敬语" aria-label="Permalink to &quot;敬语&quot;">​</a></h1><h2 id="敬语的种类" tabindex="-1">敬语的种类 <a class="header-anchor" href="#敬语的种类" aria-label="Permalink to &quot;敬语的种类&quot;">​</a></h2><p>敬语主要分为三类：</p><ol><li><p><strong><ruby>尊敬语<rt>そんけいご</rt></ruby></strong></p><blockquote><p>人の動作・状態・持ち物などを高める。</p></blockquote><blockquote><p>對說話對象的行為使用。以「抬舉」說話對象來表現敬意。</p></blockquote><blockquote><p>尊敬语代表尊敬话题里施行行为的人。话题提及社会阶级较高的人，例如长辈、师长、上司、客户等，便会使用尊敬语。提及说话人自身的时候，不用尊敬语。</p></blockquote></li><li><p><strong><ruby>谦让语<rt>けんじょうご</rt></ruby></strong></p><blockquote><p>話し手（書き手）が自分の側の動作などをへりくだる（低くする）。</p></blockquote><blockquote><p>對自己的行為使用。面對說話對象，以自己的謙遜來表現敬意。</p></blockquote><blockquote><p>通过贬低自己的行为来抬高对方，以表达对对方的敬意。</p></blockquote></li><li><p><strong><ruby>丁寧語<rt>ていねいご</rt></ruby></strong></p><blockquote><p>話し手（書き手）が言い方を丁寧にする。</p></blockquote><blockquote><p>使用禮貌（丁寧）的措辭來對對方表示敬意。既不「抬舉」說話對象（尊敬語），也不「降低」自己（謙讓語）。</p></blockquote><blockquote><p>通过使用礼貌的言辞来表达敬意，无论对象是否需要被抬高都可以使用的语言。</p></blockquote></li></ol><h2 id="主要动词的敬语表" tabindex="-1">主要动词的敬语表 <a class="header-anchor" href="#主要动词的敬语表" aria-label="Permalink to &quot;主要动词的敬语表&quot;">​</a></h2><p>主な動詞の敬語表</p><table tabindex="0"><thead><tr><th style="text-align:center;">意味</th><th style="text-align:center;">尊敬語</th><th style="text-align:center;">謙譲語</th><th style="text-align:center;">丁寧語</th></tr></thead><tbody><tr><td style="text-align:center;">来る</td><td style="text-align:center;">いらっしゃる<br>お越しになる<br>お見えになる<br>おいでになる</td><td style="text-align:center;">参る</td><td style="text-align:center;">来ます</td></tr><tr><td style="text-align:center;">行く</td><td style="text-align:center;">いらっしゃる<br>おいでになる</td><td style="text-align:center;">参る</td><td style="text-align:center;">行きます</td></tr><tr><td style="text-align:center;">見る</td><td style="text-align:center;">ご覧になる</td><td style="text-align:center;">拝見する</td><td style="text-align:center;">見ます</td></tr><tr><td style="text-align:center;">食べる</td><td style="text-align:center;">召し上がる<br>お上がりになる</td><td style="text-align:center;">いただく<br>頂戴する</td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;">会う</td><td style="text-align:center;">お会いになる</td><td style="text-align:center;">お目にかかる</td><td style="text-align:center;">会います</td></tr><tr><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td></tr><tr><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td><td style="text-align:center;"></td></tr></tbody></table>',7)]))}const b=e(d,[["render",a]]);export{x as __pageData,b as default};
